def count_houses_visited(instructions) -> int:
    """Takes a string that contains the instructions and returns the number
    of the houses that received at least one present.
    """
    x, y = 0, 0
    houses_visited = [(0,0)]

    for character in instructions:
        if character == '^' : # N
            y += 1
        elif character == 'v': # S
            y -= 1
        elif character == '>': # E
            x += 1
        elif character == '<': # W
            x -= 1
        houses_visited.append((x,y))

    number_of_houses = len(set(houses_visited))

    return number_of_houses

def count_houses_visited2(instructions) -> int:
    """Takes a string that contains the instructions and returns the number
    of the houses that received at least one present.
    """
    x1, y1 = 0, 0
    x2, y2 = 0, 0
    houses_visited = [(0,0)]

    for i, character in enumerate(instructions):
        if i % 2 == 0:
            if character == '^' : # N
                y1 += 1
            elif character == 'v': # S
                y1 -= 1
            elif character == '>': # E
                x1 += 1
            elif character == '<': # W
                x1 -= 1
            houses_visited.append((x1,y1))
        else:
            if character == '^' : # N
                y2 += 1
            elif character == 'v': # S
                y2 -= 1
            elif character == '>': # E
                x2 += 1
            elif character == '<': # W
                x2 -= 1
            houses_visited.append((x2,y2))

    number_of_houses = len(set(houses_visited))

    return number_of_houses

# Test Cases Part One
assert count_houses_visited('>') == 2
assert count_houses_visited('^>v<') == 4
assert count_houses_visited('^v^v^v^v^v') == 2

print(f'Output for MB: {count_houses_visited(test_input_mb)}')
print(f'Output for MP: {count_houses_visited(test_input_mp)}')

# Test Cases Part Two
assert count_houses_visited2('^v') == 3
assert count_houses_visited2('^>v<') == 3
assert count_houses_visited2('^v^v^v^v^v') == 11

print(f'Output for MB: {count_houses_visited2(test_input_mb)}')
print(f'Output for MP: {count_houses_visited2(test_input_mp)}')
